(76, 177, '110', '680', '6', '180', '120', '#999999', '<p style="text-align: center;">\n	<strong>Rappeler les parties demanderesses et défenderesses ''à l''ordre''</strong>\n</p>\n<p style="text-align: center;">\n	(lignes 18 à 19; 44 à 45)\n</p>', '', 55),
(77, 177, '240', '680', '7', '240', '100', '#999999', '<p style="text-align: center;">\n	<strong>Analyser si les conditions propres à chaque qualification juridiques sont réalisées, afin d''en déduire un effet</strong>\n</p>\n<p style="text-align: center;">\n	(lignes 25 à 26)\n</p>', '', 55),
(78, 174, '280.000015259', '40', '10', '941', '51', '#999999', '<p style="text-align: center;">\n	<span style="font-size: 20px;"></span><span style="font-size: 20px; background-color: initial;">Dispositif du jugement</span>\n</p>', '', 55),
(79, 178, '30', '210', '1', '430', '50', '#aa007c', '<p style="text-align: center;">\n	 <strong>Tribunal Fédéral</strong>\n</p>', '', 55),
