(87, 178, '470', '220', '8', '180', '50', '#83146a', '<p>\n	Prétentions concrètes\n</p>', '', 55),
(88, 178, '530', '220', '9', '180', '50', '#83146a', '<p>\n	Objet déterminée\n</p>', '', 55),
