(514, 218, '260', '470', '8', '190', '120', '#65004c', '<p>\n	 <span style="color: rgb(255, 255, 255);">Partiellement recevable: confiscation possible des biens en possession du défendeur mais impossible pour ceux aux mains des tiers ou ceux qui n''existent pas encore.</span>\n</p>', '', 55),
(515, 218, '260', '670', '9', '80', '90', '#65004c', '<p>\n	 <span style="color: rgb(255, 255, 255);">Paiement des frais par celui qui succombe </span>\n</p>', '', 55),
(519, 218, '390', '220', '10', '240', '80', '#83146a', '<p>\n	 <span style="color: rgb(255, 255, 255);">Le but d''un jugement n''est pas de rappeler la loi mais de l''appliquer. </span>\n</p>', '', 55),
(520, 218, '390', '470', '11', '190', '80', '#83146a', '<p>\n	 <span style="color: rgb(255, 255, 255);">Un jugement ne pourra pas avoir d''effets sur les tiers mais uniquement sur les parties.</span>\n</p>', '', 55),
(521, 211, '400', '210', '11', '530', '30', '#cccccc', '<p style="text-align: center;">\n	<em><span style="color: rgb(0, 0, 0);">Exemples tirés de l''ATF 121 III 64 illustrant ces fonctions</span></em>\n</p>', '', 55),
(523, 219, '40', '40', '2', '120', '40', '#666666', '<p>\n	 Conclusion 1\n</p>', '', 55),
